var Ezlog = require('ezlog'),
		log = new Ezlog({ pref: { t: '[DUMP]', c: 'blue' }});

var fs = require('fs');

module.exports = function (fn, data){
	data = data || '';
	if(typeof data === 'object') data = JSON.stringify(data, undefined, 2);

	log(fn + ', chars: ' + data.length);
	fs.writeFile(process.DUMPFOLDER + fn, data, function (err){
		if(err) return log('error @DUMP w/ fn: ' + fn);
	});
};